import { EnhancedEmailAddress } from "./types";
import { fetchEmailAddresses } from "./fetchers";
import EmailManagement1Inner from "./inner";

export type EmailManagement1Props = {
  /**
  * A list of Clerk's [EmailAddress](https://clerk.com/docs/reference/javascript/types/email-address) type
  * with a `isPrimary` boolean and an improved `linkedTo` field, with a `providerName` and `providerIcon`.
  **/
  emails?: EnhancedEmailAddress[];
};

export default function EmailManagement1({ emails }: EmailManagement1Props) {
  if (emails) {
    return <EmailManagement1Inner emails={emails} />;
  }

  return <EmailManagement1WithClerk />;
}

async function EmailManagement1WithClerk() {
  const emails = await fetchEmailAddresses();

  if (!emails || emails.length === 0) return <p>No Emails found.</p>;

  return <EmailManagement1Inner emails={JSON.parse(JSON.stringify(emails))} />;
}

"use server";

import { OrganizationCreateFormValues } from "./email-add-form";
import { auth, clerkClient } from "@clerk/nextjs/server";
import { revalidatePath } from "next/cache";

export async function createEmailAddress(data: OrganizationCreateFormValues) {
  const { userId } = await auth();

  if (!userId) {
    throw new Error("Failed to remove email");
  }

  const client = await clerkClient();
  await client.emailAddresses.createEmailAddress({
    userId,
    emailAddress: data.email,
    primary: data.isPrimary,
    verified: true,
  });

  revalidatePath("/");
}

export async function deleteEmailAddress(emailAddressId: string) {
  const { userId } = await auth();

  if (!userId) {
    throw new Error("Failed to remove email");
  }

  const client = await clerkClient();
  await client.emailAddresses.deleteEmailAddress(emailAddressId);

  revalidatePath("/");
}

export async function setEmailPrimary(emailAddressId: string) {
  const { userId } = await auth();

  if (!userId) {
    throw new Error("Failed to remove email");
  }

  const client = await clerkClient();
  await client.users.updateUser(userId, {
    primaryEmailAddressID: emailAddressId,
  });

  revalidatePath("/");
}

import { getExternalAccount } from "@/registry/lib/external-accounts-map";
import { currentUser, EmailAddress } from "@clerk/nextjs/server";
import { EnhancedEmailAddress } from "./types";

export async function fetchEmailAddresses() {
  const user = await currentUser();

  if (!user) {
    throw new Error("Failed to retrieve email addresses");
  }

  const primaryEmailId = user.primaryEmailAddressId;

  const emails = user.emailAddresses.map((email: EmailAddress) => {
    const linkedTo = email.linkedTo.map((link) => ({
      id: link.id,
      type: link.type,
      providerName: getExternalAccount(link.type).name ?? undefined,
      providerIcon: getExternalAccount(link.type).icon ?? undefined,
    }));

    return {
      ...email,
      isPrimary: email.id === primaryEmailId,
      linkedTo,
    };
  });

  return emails as EnhancedEmailAddress[];
}

import { EmailAddress } from "@clerk/backend";

export type EnhancedLinkedTo = {
  id: string;
  type: string;
  providerName?: string | null;
  providerIcon?: React.ComponentType<{ className?: string }>;
};

export type EnhancedEmailAddress = Partial<EmailAddress> & {
  isPrimary?: boolean;
  linkedTo?: EnhancedLinkedTo[];
};

"use client";

import { useState } from "react";
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
} from "@/components/ui/alert-dialog";
import { MoreHorizontalIcon } from "lucide-react";
import { Button } from "@/components/ui/button";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuGroup,
  DropdownMenuItem,
  DropdownMenuLabel,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import { toast } from "sonner";
import { Spinner } from "@/components/ui/spinner";
import { useAuth } from "@clerk/nextjs";
import { deleteEmailAddress, setEmailPrimary } from "./actions";

export function EmailActionsDropdown({
  emailId,
  isPrimary,
  isLinked,
}: {
  emailId: string;
  isPrimary: boolean;
  isLinked: boolean;
}) {
  const { isSignedIn } = useAuth();
  const [showRemoveDialog, setShowRemoveDialog] = useState(false);
  const [loading, setLoading] = useState(false);

  async function handleRemove() {
    if (!isSignedIn) {
      toast.warning("You must be logged in to use this functionality.");
    } else {
      setLoading(true);
      try {
        await deleteEmailAddress(emailId);
        toast.success("Email removed successfully.");
      } catch (err) {
        console.error(err);
        toast.error("Failed to remove email.");
      } finally {
        setLoading(false);
      }
    }
  }

  async function handleSetPrimary() {
    if (!isSignedIn) {
      toast.warning("You must be logged in to use this functionality.");
    } else {
      setLoading(true);
      try {
        await setEmailPrimary(emailId);
        toast.success("Email updated successfully.");
      } catch (err) {
        console.error(err);
        toast.error("Failed to update email.");
      } finally {
        setLoading(false);
      }
    }
  }

  return (
    <>
      <DropdownMenu modal={false}>
        <DropdownMenuTrigger asChild>
          <Button variant="outline" aria-label="Open menu" size="icon-sm">
            <MoreHorizontalIcon />
          </Button>
        </DropdownMenuTrigger>
        <DropdownMenuContent className="w-40" align="end">
          <DropdownMenuLabel>Email Actions</DropdownMenuLabel>
          <DropdownMenuSeparator />
          <DropdownMenuGroup>
            <DropdownMenuItem
              onClick={() => handleSetPrimary()}
              disabled={isPrimary}
            >
              Set as primary
            </DropdownMenuItem>
            <DropdownMenuItem
              variant="destructive"
              disabled={isPrimary || isLinked}
              onSelect={() => setShowRemoveDialog(true)}
            >
              Remove email
            </DropdownMenuItem>
          </DropdownMenuGroup>
        </DropdownMenuContent>
      </DropdownMenu>
      <AlertDialog open={showRemoveDialog} onOpenChange={setShowRemoveDialog}>
        <AlertDialogContent>
          <AlertDialogHeader>
            <AlertDialogTitle>Remove this email address?</AlertDialogTitle>
            <AlertDialogDescription>
              This email address will no longer be associated with your account
              immediately.
            </AlertDialogDescription>
          </AlertDialogHeader>
          <AlertDialogFooter>
            <AlertDialogCancel>Cancel</AlertDialogCancel>
            <AlertDialogAction asChild>
              <Button onClick={handleRemove} disabled={loading}>
                {loading ? (
                  <>
                    <Spinner />
                    <p>Removing...</p>
                  </>
                ) : (
                  "Remove"
                )}
              </Button>
            </AlertDialogAction>
          </AlertDialogFooter>
        </AlertDialogContent>
      </AlertDialog>
    </>
  );
}

"use client";

import { useState } from "react";
import { zodResolver } from "@hookform/resolvers/zod";
import { Controller, useForm } from "react-hook-form";
import * as z from "zod";
import { Plus } from "lucide-react";
import { Button } from "@/components/ui/button";
import {
  Field,
  FieldError,
  FieldGroup,
  FieldLabel,
} from "@/components/ui/field";
import { toast } from "sonner";
import {
  Dialog,
  DialogContent,
  DialogFooter,
  DialogHeader,
  DialogTitle,
  DialogClose,
  DialogTrigger,
} from "@/components/ui/dialog";
import { Input } from "@/components/ui/input";
import { Checkbox } from "@/components/ui/checkbox";
import { createEmailAddress } from "./actions";
import { Spinner } from "@/components/ui/spinner";
import { useAuth } from "@clerk/nextjs";

const emailAddFormSchema = z.object({
  email: z.email(),
  isPrimary: z.boolean(),
});

export type OrganizationCreateFormValues = z.infer<typeof emailAddFormSchema>;

export function EmailAddForm() {
  const { isSignedIn } = useAuth();
  const [showAddDialog, setShowAddDialog] = useState(false);
  const [loading, setLoading] = useState(false);

  const form = useForm<z.infer<typeof emailAddFormSchema>>({
    resolver: zodResolver(emailAddFormSchema),
    defaultValues: {
      email: "",
      isPrimary: false,
    },
  });

  async function handleAdd(data: OrganizationCreateFormValues) {
    if (!isSignedIn) {
      toast.warning("You must be logged in to use this functionality.");
    } else {
      setLoading(true);
      try {
        await createEmailAddress(data);
        setShowAddDialog(false);
        toast.success("Email created successfully.");
      } catch (err) {
        console.error(err);
        toast.error("Failed to create email.");
      } finally {
        setLoading(false);
      }
    }
  }

  function onSubmit(data: OrganizationCreateFormValues) {
    handleAdd(data);
  }

  return (
    <Dialog open={showAddDialog} onOpenChange={setShowAddDialog}>
      <DialogTrigger asChild>
        <Button variant="outline" size="sm">
          <Plus />
          Add email
        </Button>
      </DialogTrigger>
      <DialogContent className="sm:max-w-[425px]">
        <DialogHeader>
          <DialogTitle>Add a new email address</DialogTitle>
        </DialogHeader>
        <form id="form-email-add-1" onSubmit={form.handleSubmit(onSubmit)}>
          <FieldGroup className="gap-4">
            <Controller
              name="email"
              control={form.control}
              render={({ field, fieldState }) => (
                <Field data-invalid={fieldState.invalid} className="gap-1">
                  <FieldLabel htmlFor="form-email-add-1-email">
                    Email
                  </FieldLabel>
                  <Input
                    {...field}
                    id="form-email-add-1-email"
                    aria-invalid={fieldState.invalid}
                    placeholder="robertskinney@outlook.com"
                    type="email"
                    autoComplete="email"
                  />
                  {fieldState.invalid && (
                    <FieldError errors={[fieldState.error]} />
                  )}
                </Field>
              )}
            />
            <Controller
              name="isPrimary"
              control={form.control}
              render={({ field, fieldState }) => (
                <Field
                  data-invalid={fieldState.invalid}
                  orientation="horizontal"
                >
                  <Checkbox
                    id="form-email-add-1-isPrimary"
                    name={field.name}
                    checked={field.value}
                    onCheckedChange={field.onChange}
                  />
                  <FieldLabel
                    htmlFor="form-email-add-1-isPrimary"
                    className="font-normal"
                  >
                    Is Primary
                  </FieldLabel>
                  {fieldState.invalid && (
                    <FieldError errors={[fieldState.error]} />
                  )}
                </Field>
              )}
            />
          </FieldGroup>
        </form>

        <DialogFooter>
          <DialogClose asChild>
            <Button variant="outline">Cancel</Button>
          </DialogClose>
          <Button type="submit" form="form-email-add-1" disabled={loading}>
            {loading ? (
              <>
                <Spinner />
                <p>Adding...</p>
              </>
            ) : (
              "Add Email"
            )}
          </Button>
        </DialogFooter>
      </DialogContent>
    </Dialog>
  );
}
