import * as SignIn from "@clerk/elements/sign-in";
import SignInStrategyPassword from "./sign-in-strategy-password";
import SignInStrategyEmailCode from "./sign-in-strategy-email_code";
import SignInStrategyEmailLink from "./sign-in-strategy-email_link";

export type SignInVerificationsProps = {
  isGlobalLoading: boolean;
};

export default function SignInVerifications({
  isGlobalLoading,
}: SignInVerificationsProps) {
  return (
    <SignIn.Step name="verifications">
      <SignInStrategyPassword isGlobalLoading={isGlobalLoading} />

      <SignInStrategyEmailCode isGlobalLoading={isGlobalLoading} />

      <SignInStrategyEmailLink />
    </SignIn.Step>
  );
}

import * as Clerk from "@clerk/elements/common";
import * as SignIn from "@clerk/elements/sign-in";
import { Button } from "@/components/ui/button";
import {
  Card,
  CardContent,
  CardDescription,
  CardFooter,
  CardHeader,
  CardTitle,
} from "@/components/ui/card";
import { Spinner } from "@/components/ui/spinner";
import { ArrowRight, RotateCcw } from "lucide-react";

export type SignInStrategyEmailCodeProps = {
  isGlobalLoading: boolean;
};

export default function SignInStrategyEmailCode({
  isGlobalLoading,
}: SignInStrategyEmailCodeProps) {
  return (
    <SignIn.Strategy name="email_code">
      <Card className="w-full sm:w-96">
        <CardHeader>
          <CardTitle>Check your email</CardTitle>
          <CardDescription>
            Enter the verification code sent to your email. (
            <SignIn.SafeIdentifier />)
          </CardDescription>
        </CardHeader>
        <CardContent className="grid gap-y-4">
          <Clerk.Field name="code">
            <Clerk.Label className="sr-only">
              Email verification code
            </Clerk.Label>
            <div className="grid gap-y-2 items-center justify-center">
              <div className="flex justify-center text-center">
                <Clerk.Input
                  type="otp"
                  autoSubmit
                  className="flex justify-center has-disabled:opacity-50"
                  render={({ value, status }) => {
                    return (
                      <div
                        data-status={status}
                        className="relative flex h-9 w-9 items-center justify-center border-y border-r border-input text-sm shadow-sm transition-all first:rounded-l-md first:border-l last:rounded-r-md data-[status=selected]:ring-1 data-[status=selected]:ring-ring data-[status=cursor]:ring-1 data-[status=cursor]:ring-ring"
                      >
                        {value}
                      </div>
                    );
                  }}
                />
              </div>
              <Clerk.FieldError className="block text-sm text-destructive text-center" />
              <SignIn.Action
                asChild
                resend
                className="text-muted-foreground"
                fallback={({ resendableAfter }) => (
                  <Button variant="link" size="sm" disabled>
                    Didn&apos;t receive a code? Resend (
                    <span className="tabular-nums">{resendableAfter}</span>)
                  </Button>
                )}
              >
                <Button variant="link" size="sm">
                  Didn&apos;t receive a code? Resend
                </Button>
              </SignIn.Action>
            </div>
          </Clerk.Field>
        </CardContent>
        <CardFooter>
          <div className="grid w-full gap-y-4">
            <SignIn.Action submit asChild>
              <Button disabled={isGlobalLoading}>
                <Clerk.Loading>
                  {(isLoading) => {
                    return isLoading ? (
                      <Spinner />
                    ) : (
                      <>
                        <p>Continue</p>
                        <ArrowRight />
                      </>
                    );
                  }}
                </Clerk.Loading>
              </Button>
            </SignIn.Action>
            <SignIn.Action navigate="start" asChild>
              <Button type="button" variant="outline">
                <RotateCcw />
                <p>Restart</p>
              </Button>
            </SignIn.Action>
            <SignIn.Action navigate="choose-strategy" asChild>
              <Button size="sm" variant="link">
                Use another method
              </Button>
            </SignIn.Action>
          </div>
        </CardFooter>
      </Card>
    </SignIn.Strategy>
  );
}

