import * as Clerk from "@clerk/elements/common";
import * as SignIn from "@clerk/elements/sign-in";
import { Button } from "@/components/ui/button";
import {
  Card,
  CardContent,
  CardDescription,
  CardFooter,
  CardHeader,
  CardTitle,
} from "@/components/ui/card";
import { Spinner } from "@/components/ui/spinner";
import { ArrowLeft, Key, Mail, MessageSquare } from "lucide-react";

export type SignInChooseStrategyProps = {
  isGlobalLoading: boolean;
};

export default function SignInChooseStrategy({
  isGlobalLoading,
}: SignInChooseStrategyProps) {
  return (
    <SignIn.Step name="choose-strategy">
      <Card className="w-full sm:w-96">
        <CardHeader>
          <CardTitle>Use another method</CardTitle>
          <CardDescription>
            Facing issues? You can use any of these methods to sign in.
          </CardDescription>
        </CardHeader>
        <CardContent className="grid gap-y-4">
          <SignIn.SupportedStrategy name="email_link" asChild>
            <Button
              type="button"
              variant="secondary"
              disabled={isGlobalLoading}
            >
              <Mail />
              Magic link
            </Button>
          </SignIn.SupportedStrategy>
          <SignIn.SupportedStrategy name="email_code" asChild>
            <Button
              type="button"
              variant="secondary"
              disabled={isGlobalLoading}
            >
              <MessageSquare />
              Email code
            </Button>
          </SignIn.SupportedStrategy>
          <SignIn.SupportedStrategy name="password" asChild>
            <Button
              type="button"
              variant="secondary"
              disabled={isGlobalLoading}
            >
              <Key />
              Password
            </Button>
          </SignIn.SupportedStrategy>
        </CardContent>
        <CardFooter>
          <div className="grid w-full gap-y-4">
            <SignIn.Action navigate="previous" asChild>
              <Button disabled={isGlobalLoading}>
                <Clerk.Loading>
                  {(isLoading) => {
                    return isLoading ? (
                      <Spinner />
                    ) : (
                      <>
                        <ArrowLeft />
                        Go back
                      </>
                    );
                  }}
                </Clerk.Loading>
              </Button>
            </SignIn.Action>
          </div>
        </CardFooter>
      </Card>
    </SignIn.Step>
  );
}

import * as Clerk from "@clerk/elements/common";
import * as SignIn from "@clerk/elements/sign-in";
import { Button } from "@/components/ui/button";
import {
  Card,
  CardContent,
  CardDescription,
  CardFooter,
  CardTitle,
} from "@/components/ui/card";
import Link from "next/link";
import { Spinner } from "@/components/ui/spinner";
import { ArrowRight } from "lucide-react";
import AuthSectionSeparator from "@/registry/blocks/sign-in/sign-in-2/_components/auth-section-separator";
import AuthField from "@/registry/blocks/sign-in/sign-in-2/_components/auth-field";
import { OAuthStrategy } from "@clerk/types";
import OAuthStrategyList, {
  OAuthStrategyButtonVariants,
} from "@/registry/blocks/sign-in/sign-in-2/_components/oauth-strategy-list";
import { SignInRequiredFields } from "@/registry/blocks/sign-in/sign-in-2/_components/types";
import { cva, VariantProps } from "class-variance-authority";

const signInStartVariants = cva(
  "flex w-full justify-center items-center mx-auto gap-2",
  {
    variants: {
      variant: {
        default: "",
        concise: "",
      },
    },
    defaultVariants: {
      variant: "default",
    },
  }
);

export type SignInStartVariants = VariantProps<typeof signInStartVariants>;

export type SignInStartProps = {
  enabledOAuthStrategies?: OAuthStrategy[];
  oauthStrategyButtonVariant?: OAuthStrategyButtonVariants["variant"];
  oauthStrategyButtonSize?: OAuthStrategyButtonVariants["size"];
  variant?: SignInStartVariants["variant"];
  isGlobalLoading: boolean;
  requiredFields: SignInRequiredFields;
  signUpUrl: string;
};

export default function SignInStart({
  enabledOAuthStrategies,
  oauthStrategyButtonVariant = "default",
  oauthStrategyButtonSize = "sm",
  variant = "default",
  isGlobalLoading,
  requiredFields,
  signUpUrl,
}: SignInStartProps) {
  return (
    <SignIn.Step name="start">
      <Card className="w-full sm:w-96 p-3">
        <CardContent className="flex flex-col gap-y-6 p-5">
          <div className="flex flex-col gap-y-2">
            <CardTitle>Acme, Inc.</CardTitle>
            <CardDescription>
              Please enter your credentials to continue.
            </CardDescription>
          </div>

          {variant == "default" && enabledOAuthStrategies && (
            <>
              <OAuthStrategyList
                enabledOAuthStrategies={enabledOAuthStrategies}
                isGlobalLoading={isGlobalLoading}
                variant={oauthStrategyButtonVariant}
                size={oauthStrategyButtonSize}
              />

              <AuthSectionSeparator />
            </>
          )}

          {requiredFields.map((f) => (
            <AuthField field={f} key={f} />
          ))}
        </CardContent>

        <CardFooter className="flex flex-col w-full gap-y-4">
          <SignIn.Action submit asChild>
            <Button className="w-full" disabled={isGlobalLoading}>
              <Clerk.Loading>
                {(isLoading) => {
                  return isLoading ? (
                    <Spinner />
                  ) : (
                    <>
                      <p>Continue</p>
                      <ArrowRight />
                    </>
                  );
                }}
              </Clerk.Loading>
            </Button>
          </SignIn.Action>

          {variant == "concise" && enabledOAuthStrategies && (
            <div className="w-full">
              <OAuthStrategyList
                enabledOAuthStrategies={enabledOAuthStrategies}
                isGlobalLoading={isGlobalLoading}
                variant={oauthStrategyButtonVariant}
                size={oauthStrategyButtonSize}
              />
            </div>
          )}

          <Button variant="link" size="sm" asChild>
            <Link
              href={signUpUrl}
              className="text-primary dark:text-secondary-foreground w-full text-wrap"
            >
              Don&apos;t have an account? Sign up
            </Link>
          </Button>
        </CardFooter>
      </Card>
    </SignIn.Step>
  );
}

import { getAuthFieldInfo } from "@/registry/lib/format-auth-fields";

import * as Clerk from "@clerk/elements/common";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";

export type AuthFieldProps = {
  field: string;
};

export default function AuthField({ field }: AuthFieldProps) {
  const fieldInfo = getAuthFieldInfo(field);

  return (
    <Clerk.Field
      name={
        fieldInfo.name == "emailAddress" || fieldInfo.name == "username"
          ? "identifier"
          : fieldInfo.name
      }
      className="space-y-2"
    >
      <Clerk.Label asChild>
        <Label>{fieldInfo.alias}</Label>
      </Clerk.Label>

      <Clerk.Input type={fieldInfo.key} asChild>
        <Input
          autoComplete={fieldInfo.autoComplete}
          placeholder={fieldInfo.placeholder}
          required
        />
      </Clerk.Input>

      <Clerk.FieldError className="block text-sm text-destructive" />
    </Clerk.Field>
  );
}

export default function AuthSectionSeparator() {
  return (
    <p className="flex items-center gap-x-3 text-sm text-muted-foreground before:h-px before:flex-1 before:bg-border after:h-px after:flex-1 after:bg-border">
      or
    </p>
  );
}

"use client";

import * as Clerk from "@clerk/elements/common";
import { Button } from "@/components/ui/button";
import { Spinner } from "@/components/ui/spinner";
import { OAuthProvider, OAuthStrategy } from "@clerk/types";
import { getExternalAccount } from "@/registry/lib/format-external-accounts";
import { cva, VariantProps } from "class-variance-authority";
import { cn } from "@/lib/utils";

const oauthStrategyButtonVariants = cva(
  "flex w-full justify-center items-center mx-auto gap-2",
  {
    variants: {
      variant: {
        default: "",
        iconOnly: "",
      },
      size: {
        sm: "sm",
        default: "sm",
        lg: "lg",
        icon: "icon",
      },
    },
    defaultVariants: {
      variant: "default",
      size: "sm",
    },
  }
);

export type OAuthStrategyButtonVariants = VariantProps<
  typeof oauthStrategyButtonVariants
>;

export type OAuthStrategyListProps = {
  enabledOAuthStrategies: OAuthStrategy[];
  isGlobalLoading: boolean;
  variant: OAuthStrategyButtonVariants["variant"];
  size: OAuthStrategyButtonVariants["size"];
};

export default function OAuthStrategyList({
  enabledOAuthStrategies,
  isGlobalLoading,
  variant,
  size,
}: OAuthStrategyListProps) {
  return (
    <div
      className={cn(
        "gap-3",
        variant == "default"
          ? "grid grid-cols-1 sm:grid-cols-2 grid-flow-dense"
          : "flex flex-row"
      )}
    >
      {enabledOAuthStrategies.map((s) => {
        const acc = getExternalAccount(s);
        const scope: `provider:${OAuthProvider}` = `provider:${acc.alias}`;

        return (
          <Clerk.Connection name={acc.alias} key={s} asChild>
            <Button
              variant="outline"
              type="button"
              className={cn("sm:last:odd:col-span-2 flex-1", variant)}
              size={size}
              disabled={isGlobalLoading}
            >
              <Clerk.Loading scope={scope}>
                {(isLoading) =>
                  isLoading ? (
                    <Spinner />
                  ) : (
                    <>
                      <acc.icon className="size-4.5" />
                      {variant == "default" && (
                        <span className="label">{acc.name}</span>
                      )}
                    </>
                  )
                }
              </Clerk.Loading>
            </Button>
          </Clerk.Connection>
        );
      })}
    </div>
  );
}

export type SignInIdentifier = "emailAddress" | "username";
export type SignInAdditionalField = "password";

export type SignInRequiredFields = [
  SignInIdentifier,
  ...SignInAdditionalField[]
];

"use client";

import * as Clerk from "@clerk/elements/common";
import * as SignIn from "@clerk/elements/sign-in";
import { usePathname } from "next/navigation";
import { OAuthStrategy } from "@clerk/types";
import { Skeleton } from "@/components/ui/skeleton";
import { useAuth } from "@clerk/nextjs";
import SignInStart from "@/registry/blocks/sign-in/sign-in-2/_components/steps/sign-in-start";
import SignInChooseStrategy from "@/registry/blocks/sign-in/sign-in-2/_components/steps/sign-in-choose-strategy";
import SignInVerifications from "./_components/steps/sign-in-verifications";
import { SignInRequiredFields } from "@/registry/blocks/sign-in/sign-in-2/_components/types";

export type SignIn2Props = {
  /**
   * The required to show on the main page of the component.
   * @default ["emailAddress", "password"]
   **/
  requiredFields?: SignInRequiredFields;
  /**
   * A list of enabled Clerk [OAuthStrategy](https://clerk.com/docs/reference/javascript/types/sso#o-auth-strategy) strings.
   * @example ["oauth_google", "oauth_microsoft"]
   **/
  enabledOAuthStrategies?: OAuthStrategy[];
  /**
   * The route the user can navigate to for registration.
   * @default "/sign-up"
   **/
  signUpUrl?: string;
  /**
   * Disable the functionality of this component for demo purposes.
   * @default false
   **/
  exampleMode?: boolean;
};

export default function SignIn2({
  requiredFields = ["emailAddress", "password"],
  enabledOAuthStrategies,
  signUpUrl = "/sign-up",
  exampleMode,
}: SignIn2Props) {
  const pathname = usePathname();
  const { isLoaded } = useAuth();

  if (!isLoaded) {
    return <Skeleton className="h-[350px] w-96 rounded-xl" />;
  }

  return (
    <SignIn.Root path={pathname} exampleMode={exampleMode}>
      <Clerk.Loading>
        {(isGlobalLoading) => (
          <>
            <SignInStart
              isGlobalLoading={isGlobalLoading}
              enabledOAuthStrategies={enabledOAuthStrategies}
              oauthStrategyButtonVariant={"iconOnly"}
              oauthStrategyButtonSize={"sm"}
              variant="concise"
              requiredFields={requiredFields}
              signUpUrl={signUpUrl}
            />

            <SignInChooseStrategy isGlobalLoading={isGlobalLoading} />

            <SignInVerifications isGlobalLoading={isGlobalLoading} />

            <div id="clerk-captcha" />
          </>
        )}
      </Clerk.Loading>
    </SignIn.Root>
  );
}

export type AuthField = {
  key: string;
  name: string;
  alias: string;
  autoComplete: string;
  placeholder: string;
};

export const AuthFieldMap: Record<string, AuthField> = {
  emailAddress: {
    key: "email",
    name: "emailAddress",
    alias: "Email",
    autoComplete: "email",
    placeholder: "robertskinney@outlook.com",
  },
  username: {
    key: "username",
    name: "username",
    alias: "Username",
    autoComplete: "username",
    placeholder: "robskinney",
  },
  firstName: {
    key: "firstName",
    name: "firstName",
    alias: "First Name",
    autoComplete: "given-name",
    placeholder: "Robert",
  },
  lastName: {
    key: "lastName",
    name: "lastName",
    alias: "Last Name",
    autoComplete: "family-name",
    placeholder: "Kinney",
  },
  password: {
    key: "password",
    name: "password",
    alias: "Password",
    autoComplete: "current_password",
    placeholder: "********",
  },
