import * as Clerk from "@clerk/elements/common";
import * as SignUp from "@clerk/elements/sign-up";
import { Button } from "@/components/ui/button";
import {
  Card,
  CardContent,
  CardDescription,
  CardFooter,
  CardTitle,
} from "@/components/ui/card";
import Link from "next/link";
import { Spinner } from "@/components/ui/spinner";
import { ArrowRight } from "lucide-react";
import { cva, VariantProps } from "class-variance-authority";
import { OAuthStrategy } from "@clerk/types";
import OAuthStrategyList, {
  OAuthStrategyButtonVariants,
} from "@/registry/blocks/sign-up/sign-up-1/_components/oauth-strategy-list";
import { SignUpRequiredFields } from "@/registry/blocks/sign-up/sign-up-1/_components/types";
import AuthSectionSeparator from "@/registry/blocks/sign-up/sign-up-1/_components/auth-section-separator";
import AuthField from "@/registry/blocks/sign-up/sign-up-1/_components/auth-field";

const signUpStartVariants = cva(
  "flex w-full justify-center items-center mx-auto gap-2",
  {
    variants: {
      variant: {
        default: "",
        concise: "",
      },
    },
    defaultVariants: {
      variant: "default",
    },
  }
);

export type SignUpStartVariants = VariantProps<typeof signUpStartVariants>;

export type SignUpStartProps = {
  enabledOAuthStrategies?: OAuthStrategy[];
  oauthStrategyButtonVariant?: OAuthStrategyButtonVariants["variant"];
  oauthStrategyButtonSize?: OAuthStrategyButtonVariants["size"];
  variant?: SignUpStartVariants["variant"];
  isGlobalLoading: boolean;
  requiredFields: SignUpRequiredFields;
  signInUrl: string;
};

export default function SignUpStart({
  enabledOAuthStrategies,
  oauthStrategyButtonVariant = "default",
  oauthStrategyButtonSize = "sm",
  variant = "default",
  isGlobalLoading,
  requiredFields,
  signInUrl,
}: SignUpStartProps) {
  const nameFields = requiredFields.filter((f) => f.includes("Name"));
  const otherFields = requiredFields.filter((f) => !f.includes("Name"));
  return (
    <SignUp.Step name="start">
      <Card className="w-full sm:w-96 p-3">
        <CardContent className="flex flex-col gap-y-6 p-5">
          <div className="flex flex-col gap-y-2">
            <CardTitle>Acme, Inc.</CardTitle>
            <CardDescription>
              Please fill out the form to create your account.
            </CardDescription>
          </div>

          {variant == "default" && enabledOAuthStrategies && (
            <>
              <OAuthStrategyList
                enabledOAuthStrategies={enabledOAuthStrategies}
                isGlobalLoading={isGlobalLoading}
                variant={oauthStrategyButtonVariant}
                size={oauthStrategyButtonSize}
              />

              <AuthSectionSeparator />
            </>
          )}

          {nameFields && (
            <div className="flex flex-col sm:flex-row gap-x-6">
              {nameFields.map((f) => (
                <AuthField field={f} key={f} />
              ))}
            </div>
          )}

          {otherFields.map((f) => (
            <AuthField field={f} key={f} />
          ))}
        </CardContent>

        <CardFooter className="flex flex-col w-full gap-y-4">
          <SignUp.Captcha className="empty:hidden" />
          <SignUp.Action submit asChild>
            <Clerk.Loading>
              {(isLoading) => (
                <Button className="w-full" disabled={isLoading}>
                  {isLoading ? (
                    <Spinner />
                  ) : (
                    <>
                      <p>Continue</p>
                      <ArrowRight />
                    </>
                  )}
                </Button>
              )}
            </Clerk.Loading>
          </SignUp.Action>

          {variant == "concise" && enabledOAuthStrategies && (
            <div className="w-full">
              <OAuthStrategyList
                enabledOAuthStrategies={enabledOAuthStrategies}
                isGlobalLoading={isGlobalLoading}
                variant={oauthStrategyButtonVariant}
                size={oauthStrategyButtonSize}
              />
            </div>
          )}

          <Button variant="link" size="sm" asChild>
            <Link
              href={signInUrl}
              className="text-primary dark:text-secondary-foreground w-full text-wrap"
            >
              Already have an account? Sign in
            </Link>
          </Button>
        </CardFooter>
      </Card>
    </SignUp.Step>
  );
}

import { getAuthFieldInfo } from "@/registry/lib/format-auth-fields";

import * as Clerk from "@clerk/elements/common";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";

export type AuthFieldProps = {
  field: string;
};

export default function AuthField({ field }: AuthFieldProps) {
  const fieldInfo = getAuthFieldInfo(field);

  return (
    <Clerk.Field name={fieldInfo.name} className="space-y-2 w-full">
      <Clerk.Label asChild>
        <Label>{fieldInfo.alias}</Label>
      </Clerk.Label>

      <Clerk.Input type={fieldInfo.key} asChild>
        <Input
          autoComplete={fieldInfo.autoComplete}
          placeholder={fieldInfo.placeholder}
          required
        />
      </Clerk.Input>

      <Clerk.FieldError className="block text-sm text-destructive" />
    </Clerk.Field>
  );
}

export default function AuthSectionSeparator() {
  return (
    <p className="flex items-center gap-x-3 text-sm text-muted-foreground before:h-px before:flex-1 before:bg-border after:h-px after:flex-1 after:bg-border">
      or
    </p>
  );
}

"use client";

import * as Clerk from "@clerk/elements/common";
import { Button } from "@/components/ui/button";
import { Spinner } from "@/components/ui/spinner";
import { OAuthProvider, OAuthStrategy } from "@clerk/types";
import { getExternalAccount } from "@/registry/lib/format-external-accounts";
import { cva, VariantProps } from "class-variance-authority";
import { cn } from "@/lib/utils";

const oauthStrategyButtonVariants = cva(
  "flex w-full justify-center items-center mx-auto gap-2",
  {
    variants: {
      variant: {
        default: "",
        iconOnly: "",
      },
      size: {
        sm: "sm",
        default: "sm",
        lg: "lg",
        icon: "icon",
      },
    },
    defaultVariants: {
      variant: "default",
      size: "sm",
    },
  }
);

export type OAuthStrategyButtonVariants = VariantProps<
  typeof oauthStrategyButtonVariants
>;

export type OAuthStrategyListProps = {
  enabledOAuthStrategies: OAuthStrategy[];
  isGlobalLoading: boolean;
  variant: OAuthStrategyButtonVariants["variant"];
  size: OAuthStrategyButtonVariants["size"];
};

export default function OAuthStrategyList({
  enabledOAuthStrategies,
  isGlobalLoading,
  variant,
  size,
}: OAuthStrategyListProps) {
  return (
    <div
      className={cn(
        "gap-3",
        variant == "default"
          ? "grid grid-cols-1 sm:grid-cols-2 grid-flow-dense"
          : "flex flex-row"
      )}
    >
      {enabledOAuthStrategies.map((s) => {
        const acc = getExternalAccount(s);
        const scope: `provider:${OAuthProvider}` = `provider:${acc.alias}`;

        return (
          <Clerk.Connection name={acc.alias} key={s} asChild>
            <Button
              variant="outline"
              type="button"
              className={cn("sm:last:odd:col-span-2 flex-1", variant)}
              size={size}
              disabled={isGlobalLoading}
            >
              <Clerk.Loading scope={scope}>
                {(isLoading) =>
                  isLoading ? (
                    <Spinner />
                  ) : (
                    <>
                      <acc.icon className="size-4.5" />
                      {variant == "default" && (
                        <span className="label">{acc.name}</span>
                      )}
                    </>
                  )
                }
              </Clerk.Loading>
            </Button>
          </Clerk.Connection>
        );
      })}
    </div>
  );
}

export type SignUpIdentifier = "emailAddress" | "username";
export type SignUpAdditionalField = "firstName" | "lastName" | "password";

export type SignUpRequiredFields = [
  SignUpIdentifier,
  ...SignUpAdditionalField[]
];

"use client";

import * as Clerk from "@clerk/elements/common";
import * as SignUp from "@clerk/elements/sign-up";
import { usePathname } from "next/navigation";
import { Skeleton } from "@/components/ui/skeleton";
import { useAuth } from "@clerk/nextjs";
import { SignUpRequiredFields } from "./_components/types";
import { OAuthStrategy } from "@clerk/types";
import SignUpVerifications from "./_components/steps/sign-up-verifications";
import SignUpContinue from "./_components/steps/sign-up-continue";
import SignUpStart from "./_components/steps/sign-up-start";

export type SignUp1Props = {
  /**
   * The required to show on the main page of the component.
   * @default ["emailAddress", "password"]
   **/
  requiredFields?: SignUpRequiredFields;
  /**
   * A list of enabled Clerk [OAuthStrategy](https://clerk.com/docs/reference/javascript/types/sso#o-auth-strategy) strings.
   * @example ["oauth_google", "oauth_microsoft"]
   **/
  enabledOAuthStrategies?: OAuthStrategy[];
  /**
   * The route the user can login from.
   * @default "/sign-in"
   **/
  signInUrl?: string;
  /**
   * Disable the functionality of this component for demo purposes.
   * @default false
   **/
  exampleMode?: boolean;
};

export default function SignUp1({
  requiredFields = ["emailAddress", "firstName", "lastName", "password"],
  enabledOAuthStrategies = ["oauth_google", "oauth_microsoft"],
  exampleMode,
  signInUrl = "/sign-in",
}: SignUp1Props) {
  const pathname = usePathname();
  const { isLoaded } = useAuth();

  // Show loading state when autoConfig is enabled and data is loading
  if (!isLoaded) {
    return <Skeleton className="h-[350px] w-96 rounded-xl" />;
  }

  return (
    <SignUp.Root path={pathname} exampleMode={exampleMode}>
      <Clerk.Loading>
        {(isGlobalLoading) => (
          <>
            <SignUpStart
              isGlobalLoading={isGlobalLoading}
              enabledOAuthStrategies={enabledOAuthStrategies}
              oauthStrategyButtonVariant="iconOnly"
              oauthStrategyButtonSize={"sm"}
              variant="concise"
              requiredFields={requiredFields}
              signInUrl={signInUrl}
            />

            <SignUpContinue />

            <SignUpVerifications isGlobalLoading={isGlobalLoading} />

            <div id="clerk-captcha" />
          </>
        )}
      </Clerk.Loading>
    </SignUp.Root>
  );
}

