"use server";

import { auth, clerkClient } from "@clerk/nextjs/server";
import { revalidatePath } from "next/cache";

export async function deleteSocialAccount(externalAccountId: string) {
  const { userId } = await auth();

  if (!userId) {
    throw new Error("Failed to remove social account");
  }

  const client = await clerkClient();
  await client.users.deleteUserExternalAccount({
    userId,
    externalAccountId,
  });

  revalidatePath("/");
}

import {
  getExternalAccount,
} from "@/registry/lib/external-accounts-map";
import { currentUser, ExternalAccount } from "@clerk/nextjs/server";
import { EnhancedExternalAccount } from "./types";

export async function fetchSocialAccounts() {
  const user = await currentUser();

  if (!user) {
    throw new Error("Failed to retrieve social accounts.");
  }

  const accounts = user.externalAccounts.map((account: ExternalAccount) => {
    return {
      ...account,
      providerName: getExternalAccount(account.provider).name ?? null,
      providerIcon: getExternalAccount(account.provider).icon ?? null,
    };
  });

  return accounts as EnhancedExternalAccount[];
}

import { fetchSocialAccounts } from "./fetchers";
import { SocialAccounts1Inner } from "./inner";
import { EnhancedExternalAccount } from "./types";
import { OAuthStrategy } from "@clerk/types";

export type SocialAccounts1Props = {
  /**
  * A list of enabled Clerk [OAuthStrategy](https://clerk.com/docs/reference/javascript/types/sso#o-auth-strategy) strings.
  * @example ["oauth_google", "oauth_microsoft"]
  **/
  enabledStrategies: OAuthStrategy[];
  /**
  * A list of Clerk's [ExternalAccount](https://clerk.com/docs/reference/javascript/types/external-account) object
  * enhanced with a frontend-ready `providerName` and `providerIcon`.
  **/
  accounts?: EnhancedExternalAccount[];
  /**
  * The URL to redirect to after successfully linking an account.
  * @default "/"
  **/
  redirectUrl?: string;
};

export default function SocialAccounts1({
  enabledStrategies,
  accounts,
  redirectUrl = "/",
}: SocialAccounts1Props) {
  if (accounts) {
    return (
      <SocialAccounts1Inner
        accounts={JSON.parse(JSON.stringify(accounts))}
        enabledStrategies={enabledStrategies}
        redirectUrl={redirectUrl}
      />
    );
  }

  return (
    <SocialAccounts1WithClerk
      enabledStrategies={enabledStrategies}
      redirectUrl={redirectUrl}
    />
  );
}

async function SocialAccounts1WithClerk({
  enabledStrategies,
  redirectUrl,
}: {
  enabledStrategies: OAuthStrategy[];
  redirectUrl: string;
}) {
  const accounts = await fetchSocialAccounts();

  return (
    <SocialAccounts1Inner
      accounts={JSON.parse(JSON.stringify(accounts))}
      enabledStrategies={enabledStrategies}
      redirectUrl={redirectUrl}
    />
  );
}

"use client";

import { Card, CardContent, CardTitle } from "@/components/ui/card";
import { ScrollArea, ScrollBar } from "@/components/ui/scroll-area";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import { Button } from "@/components/ui/button";
import { getExternalAccount } from "@/registry/lib/external-accounts-map";
import { OAuthStrategy, SessionVerificationLevel } from "@clerk/types";
import { EnhancedExternalAccount } from "./types";
import { DotIcon } from "lucide-react";
import { SocialAccountActionsDropdown } from "./actions-dropdown";
import { useClerk, useReverification, useUser } from "@clerk/nextjs";
import { useRouter } from "next/navigation";
import { toast } from "sonner";
import { useState } from "react";
import { VerificationComponent } from "@/registry/blocks/verification-component";
import {
  isClerkRuntimeError,
  isReverificationCancelledError,
} from "@clerk/nextjs/errors";
import { Spinner } from "@/components/ui/spinner";

export function SocialAccounts1Inner({
  accounts,
  enabledStrategies,
  redirectUrl,
}: {
  accounts: Partial<EnhancedExternalAccount>[];
  enabledStrategies: OAuthStrategy[];
  redirectUrl: string;
}) {
  const linkedAccounts = new Map(accounts.map((a) => [a.providerName, a]));

  const [linkingProvider, setLinkingProvider] = useState<OAuthStrategy | null>(
    null
  );
  const { user, isLoaded } = useUser();

  const [verificationState, setVerificationState] = useState<
    | {
        complete: () => void;
        cancel: () => void;
        level: SessionVerificationLevel | undefined;
        inProgress: boolean;
      }
    | undefined
  >(undefined);

  const router = useRouter();

  const createExternalAccount = useReverification(
    (strategy: OAuthStrategy) =>
      user?.createExternalAccount({
        strategy,
        redirectUrl: redirectUrl,
      }),
    {
      onNeedsReverification: ({ complete, cancel, level }) => {
        setVerificationState({
          complete,
          cancel,
          level,
          inProgress: true,
        });
      },
    }
  );

  const handleLink = async (strategy: OAuthStrategy) => {
    setLinkingProvider(strategy);
    try {
      await createExternalAccount(strategy).then((res) => {
        if (res?.verification?.externalVerificationRedirectURL) {
          router.push(res.verification.externalVerificationRedirectURL.href);
        }
      });
    } catch (e) {
      console.error(e);
      // console.log(err);
      if (isClerkRuntimeError(e) && isReverificationCancelledError(e)) {
        toast.info("Verification cancelled.");
      } else {
        toast.error("Failed to connect to OAuth Provider.");
      }
    } finally {
      setLinkingProvider(null);
    }
  };
  //   if (!isLoaded) return <p>Loading...</p>;

  const { session } = useClerk();

  if (!user || !isLoaded || !session) return;

  return (
    <>
      <Card className="w-full p-3">
        <CardContent className="flex flex-col gap-y-4 p-5">
          <CardTitle className="flex flex-row">
            <h1>Social Accounts</h1>
          </CardTitle>
          <ScrollArea>
            <Table className="mb-3">
              <TableHeader>
                <TableRow>
                  <TableHead>Provider</TableHead>
                  <TableHead className="text-right">Actions</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {enabledStrategies.map((providerKey) => {
                  const providerInfo = getExternalAccount(providerKey);
                  const Icon = providerInfo.icon;

                  const userAccount = linkedAccounts.get(providerInfo.name);
                  const isLinked = !!userAccount;

                  return (
                    <TableRow key={providerKey}>
                      <TableCell>
                        <div className="flex flex-row items-center h-full gap-2">
                          <Icon className="size-5" />
                          <div className="flex flex-row items-center gap-1">
                            <p>{providerInfo.name}</p>
                            {isLinked && (
                              <>
                                <DotIcon className="size-4 stroke-muted-foreground" />
                                <p className="text-muted-foreground">
                                  {userAccount?.emailAddress}
                                </p>
                              </>
                            )}
                          </div>
                        </div>
                      </TableCell>

                      <TableCell className="text-right">
                        {isLinked ? (
                          <SocialAccountActionsDropdown
                            accountId={userAccount.id!}
                          />
                        ) : (
                          <Button
                            disabled={!!linkingProvider}
                            onClick={() => handleLink(providerInfo.key)}
                            variant="outline"
                            size="sm"
                          >
                            {linkingProvider === providerInfo.key ? (
                              <>
                                <Spinner />
                                <p>Linking...</p>
                              </>
                            ) : (
                              "Link"
                            )}
                          </Button>
                        )}
                      </TableCell>
                    </TableRow>
                  );
                })}
              </TableBody>
            </Table>
            <ScrollBar orientation="horizontal" />
          </ScrollArea>
        </CardContent>
      </Card>
      {verificationState?.inProgress && (
        <VerificationComponent
          level={verificationState?.level}
          onComplete={() => {
            verificationState.complete();
            setVerificationState(undefined);
          }}
          onCancel={() => {
            verificationState.cancel();
            setVerificationState(undefined);
          }}
        />
      )}
    </>
  );
}

"use client";

import { useState } from "react";
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
} from "@/components/ui/alert-dialog";
import { MoreHorizontalIcon } from "lucide-react";
import { Button } from "@/components/ui/button";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuGroup,
  DropdownMenuItem,
  DropdownMenuLabel,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import { toast } from "sonner";
import { Spinner } from "@/components/ui/spinner";
import { useAuth } from "@clerk/nextjs";
import { deleteSocialAccount } from "./actions";

export function SocialAccountActionsDropdown({
  accountId,
}: {
  accountId: string;
}) {
  const { isSignedIn } = useAuth();
  const [showRemoveDialog, setShowRemoveDialog] = useState(false);
  const [loading, setLoading] = useState(false);

  async function handleRemove() {
    if (!isSignedIn) {
      toast.warning("You must be logged in to use this functionality.");
    } else {
      setLoading(true);
      try {
        await deleteSocialAccount(accountId);
        toast.success("Account removed successfully.");
      } catch (err) {
        console.error(err);
        toast.error("Failed to remove account.");
      } finally {
        setLoading(false);
      }
    }
  }

  return (
    <>
      <DropdownMenu modal={false}>
        <DropdownMenuTrigger asChild>
          <Button variant="outline" aria-label="Open menu" size="icon-sm">
            <MoreHorizontalIcon />
          </Button>
        </DropdownMenuTrigger>
        <DropdownMenuContent className="w-40" align="end">
          <DropdownMenuLabel>Email Actions</DropdownMenuLabel>
          <DropdownMenuSeparator />
          <DropdownMenuGroup>
            <DropdownMenuItem
              variant="destructive"
              onSelect={() => setShowRemoveDialog(true)}
              disabled={accountId.startsWith("idn")}
            >
              Remove account
            </DropdownMenuItem>
          </DropdownMenuGroup>
        </DropdownMenuContent>
      </DropdownMenu>
      <AlertDialog open={showRemoveDialog} onOpenChange={setShowRemoveDialog}>
        <AlertDialogContent>
          <AlertDialogHeader>
            <AlertDialogTitle>Remove this account?</AlertDialogTitle>
            <AlertDialogDescription>
              This social account will no longer be associated with your account
              immediately.
            </AlertDialogDescription>
          </AlertDialogHeader>
          <AlertDialogFooter>
            <AlertDialogCancel>Cancel</AlertDialogCancel>
            <AlertDialogAction asChild>
              <Button onClick={handleRemove} disabled={loading}>
                {loading ? (
                  <>
                    <Spinner />
                    <p>Removing...</p>
                  </>
                ) : (
                  "Remove"
                )}
              </Button>
            </AlertDialogAction>
          </AlertDialogFooter>
        </AlertDialogContent>
      </AlertDialog>
    </>
  );
}

import { ExternalAccountsMap } from "@/registry/lib/external-accounts-map";
import { ExternalAccount } from "@clerk/backend";

export type EnhancedExternalAccount = Partial<ExternalAccount> & {
  /**
  * The formatted name of the OAuth provider.
  **/
  providerName?: string;
  /**
  * An icon component for the OAuth provider.
  **/
  providerIcon?: React.ComponentType<{ className?: string }>;
};

export type AvailableExternalProviders = (keyof typeof ExternalAccountsMap)[];

"use client";

import { useEffect, useRef, useState } from "react";
import { useSession } from "@clerk/nextjs";
import {
  EmailCodeFactor,
  SessionVerificationLevel,
  SessionVerificationResource,
} from "@clerk/types";
import {
  AlertDialog,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
} from "@/components/ui/alert-dialog";
import {
  InputOTP,
  InputOTPGroup,
  InputOTPSlot,
} from "@/components/ui/input-otp";
import z from "zod";
import { Controller, useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { toast } from "sonner";
import { Field, FieldError, FieldLabel } from "@/components/ui/field";
import { Spinner } from "@/components/ui/spinner";
import { Button } from "@/components/ui/button";
import { REGEXP_ONLY_DIGITS } from "input-otp";

const verificationFormSchema = z.object({
  code: z.string().min(6, {
    message: "Please enter your full one-time password.",
  }),
});

export type VerificationFormValues = z.infer<typeof verificationFormSchema>;

export function VerificationComponent({
  level = "first_factor",
  onComplete,
  onCancel,
}: {
  level: SessionVerificationLevel | undefined;
  onComplete: () => void;
  onCancel: () => void;
}) {
  const form = useForm<VerificationFormValues>({
    resolver: zodResolver(verificationFormSchema),
    defaultValues: {
      code: "",
    },
  });

  const { session } = useSession();
  const [open, setOpen] = useState<boolean>(false);
  const [loading, setLoading] = useState<boolean>(false);

  const reverificationRef = useRef<SessionVerificationResource | undefined>(
    undefined
  );

  const [determinedStartingFirstFactor, setDeterminedStartingFirstFactor] =
    useState<EmailCodeFactor | undefined>();

  useEffect(() => {
    if (reverificationRef.current) {
      return;
    }

    session?.startVerification({ level }).then(async (response) => {
      reverificationRef.current = response;
      await prepareEmailVerification(response);
    });
  }, []);

  const prepareEmailVerification = async (
    verificationResource: SessionVerificationResource
  ) => {
    // To simplify the example we will only handle the first factor verification
    if (verificationResource.status === "needs_first_factor") {
      // Determine the starting first factor from the supported first factors
      const determinedStartingFirstFactor =
        verificationResource.supportedFirstFactors?.filter(
          (factor) => factor.strategy === "email_code"
        )[0];

      if (determinedStartingFirstFactor) {
        setDeterminedStartingFirstFactor(determinedStartingFirstFactor);
        // Prepare the first factor verification with the determined starting first factor
        await session?.prepareFirstFactorVerification({
          strategy: determinedStartingFirstFactor.strategy,
          emailAddressId: determinedStartingFirstFactor?.emailAddressId,
        });
      }
    }
  };

  useEffect(() => {
    if (determinedStartingFirstFactor) {
      setOpen(true);
    }
  }, [determinedStartingFirstFactor]);

  async function onSubmit(data: VerificationFormValues) {
    setLoading(true);
    try {
      await session?.attemptFirstFactorVerification({
        strategy: "email_code",
        code: data.code,
      });
      onComplete();
      toast.success("Session verified successfully.");
    } catch (e) {
      console.error(e);
      toast.error("An error occurred verifying session.");
    } finally {
      setLoading(false);
    }
  }

  if (!determinedStartingFirstFactor) {
    return null;
  }

  return (
    <AlertDialog
      open={open}
      onOpenChange={(isOpen) => {
        if (!isOpen) {
          onCancel();
        }
        setOpen(isOpen);
      }}
    >
      <AlertDialogContent>
        <AlertDialogHeader className="gap-1">
          <AlertDialogTitle>Verify your Account</AlertDialogTitle>
          <AlertDialogDescription>
            Please enter the verification code sent to{" "}
            {determinedStartingFirstFactor.safeIdentifier ||
              "your email address"}
            {"."}{" "}
          </AlertDialogDescription>
        </AlertDialogHeader>
        {/* <input
          type="number"
          name="code"
          onChange={(e) => setCode(e.target.value)}
        /> */}
        <form id="verify-email-1" onSubmit={form.handleSubmit(onSubmit)}>
          <Controller
            name="code"
            control={form.control}
            render={({ field, fieldState }) => (
              <Field data-invalid={fieldState.invalid} className="gap-1">
                <FieldLabel htmlFor="form-email-add-1-email">Code</FieldLabel>
                {/* <FormControl> */}
                <InputOTP maxLength={6} {...field} pattern={REGEXP_ONLY_DIGITS}>
                  <InputOTPGroup>
                    <InputOTPSlot index={0} />
                    <InputOTPSlot index={1} />
                    <InputOTPSlot index={2} />
                    <InputOTPSlot index={3} />
                    <InputOTPSlot index={4} />
                    <InputOTPSlot index={5} />
                  </InputOTPGroup>
                </InputOTP>
                {/* </FormConxtrol> */}

                {fieldState.invalid && (
                  <FieldError errors={[fieldState.error]} />
                )}
              </Field>
            )}
          />
        </form>

        <AlertDialogFooter>
          <AlertDialogCancel onClick={() => onCancel()}>
            Cancel
          </AlertDialogCancel>
          <Button disabled={loading} type="submit" form="verify-email-1">
            {loading ? (
              <>
                <Spinner />
                <p>Submitting...</p>
              </>
            ) : (
              "Submit"
            )}
          </Button>
        </AlertDialogFooter>
      </AlertDialogContent>
    </AlertDialog>
  );
}

import { OAuthProvider, OAuthStrategy } from "@clerk/types";
import {
  FaGoogle,
  FaMicrosoft,
  FaGithub,
  FaApple,
  FaFacebook,
  FaLinkedin,
} from "react-icons/fa";

export type ExternalProviderInfo = {
  key: OAuthStrategy;
  alias: OAuthProvider;
  name: string;
  icon: React.ComponentType<{ className?: string }>;
};

export const ExternalAccountsMap: Record<string, ExternalProviderInfo> = {
  oauth_google: {
    key: "oauth_google",
    alias: "google",
    name: "Google",
    icon: FaGoogle,
  },
  oauth_microsoft: {
    key: "oauth_microsoft",
    alias: "microsoft",
    name: "Microsoft",
    icon: FaMicrosoft,
  },
  oauth_github: {
    key: "oauth_github",
    alias: "github",
    name: "GitHub",
    icon: FaGithub,
  },
  oauth_apple: {
    key: "oauth_apple",
    alias: "apple",
    name: "Apple",
    icon: FaApple,
  },
  oauth_facebook: {
    key: "oauth_facebook",
    alias: "facebook",
    name: "Facebook",
    icon: FaFacebook,
  },
  oauth_linkedin: {
    key: "oauth_linkedin",
    alias: "linkedin",
    name: "LinkedIn",
    icon: FaLinkedin,
  },
};

export function getExternalAccount(provider: string) {
  const key = provider.startsWith("oauth_") ? provider : `oauth_${provider}`;
  return ExternalAccountsMap[key];
}
